import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Headers.css';

const Header = ({ wishlist, cart, user, setUser, setMainPageTitle }) => {
    const handleLogout = () => {
        setUser(null);
    };

    const [isOpen, setIsOpen] = useState(false);

    const toggleDropdown = () => {
        setIsOpen((prev) => !prev);
    };

    const handleLinkClick = (title) => {
        setIsOpen(false); // Close dropdown on link click
        setMainPageTitle(title); // Set the main page title based on the link
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container p-3">
                <Link className="navbar-brand" to="/">
                    <img src='./logo.png' alt='logo' />
                </Link>
                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav ms-auto">
                        <li className="nav-item dropdown">
                            <button
                                className="nav-link dropdown-toggle font-weight-bold"
                                onClick={toggleDropdown}
                                aria-expanded={isOpen}
                                style={{ background: 'none', border: 'none', cursor: 'pointer' }}
                            >
                                Shop
                            </button>
                            {isOpen && (
                                <ul className="dropdown-menu show">
                                    <li>
                                        <Link className="dropdown-item" to="/shop/electronics" onClick={() => handleLinkClick('Electronics')}>
                                            Electronics
                                        </Link>
                                    </li>
                                    <li>
                                        <Link className="dropdown-item" to="/shop/jewelery" onClick={() => handleLinkClick('Jewelry')}>
                                            Jewelry
                                        </Link>
                                    </li>
                                    <li>
                                        <Link className="dropdown-item" to="/shop/men's-clothing" onClick={() => handleLinkClick("Men's Clothing")}>
                                            Men's Clothing
                                        </Link>
                                    </li>
                                    <li>
                                        <Link className="dropdown-item" to="/shop/women's-clothing" onClick={() => handleLinkClick("Women's Clothing")}>
                                            Women's Clothing
                                        </Link>
                                    </li>
                                </ul>
                            )}
                        </li>
                        {/* Other nav items remain the same */}
                    </ul>
                </div>
            </div>
        </nav>
    );
};

export default Header;
