import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './Product.css';

const WishlistCartButton = ({ item, addToWishlist, addToCart, wishlist, cart }) => {
    const isInWishlist = wishlist.some((wishlistItem) => wishlistItem.id === item.id);
    const isInCart = cart.some((cartItem) => cartItem.id === item.id);

    return (
        <>
            <button
                className="btn btn-outline-warning me-2"
                onClick={() => addToWishlist(item)}
                disabled={isInWishlist}
            >
                <i className={`fas fa-heart ${isInWishlist ? 'text-danger' : ''}`}></i>
            </button>
            <button
                className="btn btn-outline-success"
                onClick={() => addToCart(item)}
                disabled={isInCart}
            >
                <i className={`fas fa-shopping-cart ${isInCart ? 'text-success' : ''}`}></i>
            </button>
        </>
    );
};

const Products = ({ addToWishlist, addToCart, wishlist, cart }) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetch('https://fakestoreapi.com/products')
            .then((response) => response.json())
            .then((data) => {
                setProducts(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching products:', error);
                setLoading(false);
                alert('Failed to fetch products. Please try again later.');
            });
    }, []);

    if (loading) return (
        <div className="text-center">
            <div className="spinner-border" role="status">
                <span className="visually-hidden">Loading...</span>
            </div>
        </div>
    );

    return (
        <div className="container mt-4">
            <h1 className="text-center mb-5 mt-5 hover-underline">Product List</h1>
            <div className="row">
                {products.map((product) => (
                    <div className="col-lg-3 col-md-4 col-sm-6 mb-4" key={product.id}>
                        <div className="card h-100">
                            <img
                                src={product.image}
                                className="card-img-top"
                                alt={product.title}
                                style={{ height: '250px', objectFit: 'contain' }}
                            />
                            <div className="card-body">
                                <h5 className="card-title">{product.title}</h5>
                                <p className="card-text">${product.price}</p>
                                <Link to={`/product/${product.id}`} className="btn btn-primary mb-2 me-2">
                                    View Details
                                </Link>
                                <WishlistCartButton
                                    item={product}
                                    addToWishlist={addToWishlist}
                                    addToCart={addToCart}
                                    wishlist={wishlist}
                                    cart={cart}
                                />
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Products;
