// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import ProductDetails from './products/ProductDetails';
import Wishlist from './wishlist/Wishlist';
import Cart from './cart/Cart';
import ProductList from './products/ProductList';
import Header from './header/Headers';
import Login from './signin/Login';
import Register from './signin/Register';
import Footer from './footer/Footers';
import Loading from './loading/Loadinf';


const App = () => {
  const [user, setUser] = useState(null);
  const [wishlist, setWishlist] = useState([]);
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);

  const addToWishlist = (product) => {
    setWishlist((prevWishlist) => [...prevWishlist, product]);
  };

  const removeFromWishlist = (id) => {
    setWishlist((prevWishlist) => prevWishlist.filter((item) => item.id !== id));
  };

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  const removeFromCart = (id) => {
    setCart((prevCart) => prevCart.filter((item) => item.id !== id));
  };

  useEffect(() => {
    const loadData = async () => {
      // Simulating an API call or other loading process
      await new Promise(resolve => setTimeout(resolve, 2000)); // Adjust the delay as needed
      setLoading(false);
    };

    loadData();
  }, []);

  if (loading) {
    return <Loading />; // Show loading spinner while loading
  }

  return (
    <Router>
      <Header wishlist={wishlist} cart={cart} user={user} setUser={setUser} />
      <div>
        <Routes>
          <Route path="/" element={<ProductList addToWishlist={addToWishlist} addToCart={addToCart} wishlist={wishlist} cart={cart} />} />
          <Route path="/product/:id" element={<ProductDetails addToWishlist={addToWishlist} addToCart={addToCart} wishlist={wishlist} cart={cart} />} />
          <Route path="/wishlist" element={<Wishlist wishlist={wishlist} removeFromWishlist={removeFromWishlist} />} />
          <Route path="/cart" element={<Cart cart={cart} removeFromCart={removeFromCart} />} />
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/register" element={<Register />} />
        </Routes>
      </div>
      <Footer />
    </Router>
  );
};

export default App;
