// Header.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import "./Headers.css"

const Header = ({ wishlist, cart, user, setUser }) => {
    const handleLogout = () => {
        setUser(null);
        // Add any additional logout logic here if needed
    };

    const [isOpen, setIsOpen] = useState(false);

    const toggleDropdown = () => {
        setIsOpen((prev) => !prev);
    };

    const handleLinkClick = () => {
        setIsOpen(false); // Close dropdown on link click
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container p-3">
                <Link className="navbar-brand" to="/">
                    <img src='./logo.png' alt='logo' /> {/* Adjust logo source as necessary */}
                </Link>
                <button
                    className="navbar-toggler"
                    type="button"
                    onClick={toggleDropdown}
                    aria-expanded={isOpen}
                    aria-label="Toggle navigation"
                >
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className={`collapse navbar-collapse ${isOpen ? 'show' : ''}`}>
                    <ul className="navbar-nav ms-auto">
                        <li className="nav-item dropdown">
                            <button
                                className="nav-link dropdown-toggle font-weight-bold"
                                onClick={toggleDropdown}
                                aria-expanded={isOpen}
                                style={{ background: 'none', border: 'none', cursor: 'pointer' }}
                            >
                                Shop
                            </button>
                            {isOpen && (
                                <ul className="dropdown-menu" style={dropdownStyle}>
                                    <li>
                                        <Link className="dropdown-item" to="/shop/electronics" onClick={handleLinkClick}>
                                            Electronics
                                        </Link>
                                    </li>
                                    <li>
                                        <Link className="dropdown-item" to="/shop/jewelery" onClick={handleLinkClick}>
                                            Jewelry
                                        </Link>
                                    </li>
                                    <li>
                                        <Link className="dropdown-item" to="/shop/men's-clothing" onClick={handleLinkClick}>
                                            Men's Clothing
                                        </Link>
                                    </li>
                                    <li>
                                        <Link className="dropdown-item" to="/shop/women's-clothing" onClick={handleLinkClick}>
                                            Women's Clothing
                                        </Link>
                                    </li>
                                </ul>
                            )}
                        </li>

                        <li className="nav-item">
                            <Link className="nav-link" to="/wishlist">
                                <i className="fas fa-heart"></i>
                                {wishlist.length > 0 && (
                                    <span className="badge bg-danger ms-1">{wishlist.length}</span>
                                )}
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="/cart">
                                <i className="fas fa-shopping-cart"></i>
                                {cart.length > 0 && (
                                    <span className="badge bg-danger ms-1">{cart.length}</span>
                                )}
                            </Link>
                        </li>

                        {/* Conditional Rendering for Login/Logout */}
                        {user ? (
                            <li className="nav-item">
                                <span className="nav-link">Welcome, {user.username}</span>
                                <button onClick={handleLogout} className="btn btn-link nav-link">
                                    Logout
                                </button>
                            </li>
                        ) : (
                            <li className="nav-item">
                                <Link className="nav-link" to="/login">
                                    <i className="fas fa-user"></i> Login
                                </Link>
                            </li>
                        )}
                        <li className="nav-item">
                            <Link className="btn btn-outline-primary" to="/register">
                                Register
                            </Link>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    );
};

// Inline styles for dropdown menu
const dropdownStyle = {
    position: 'absolute',
    top: '100%', // Position below the button
    left: '0',
    backgroundColor: 'white',
    border: '1px solid #ccc',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
    listStyle: 'none',
    padding: '0',
    margin: '0',
    zIndex: 1000, // Ensure it appears above other content
};

export default Header;
