import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Headers.css';
import logo from './img/zando-logo-small.png'

const Header = ({ wishlist, cart, user, setUser }) => {
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        // Fetch categories from the API
        fetch('https://fakestoreapi.com/products/categories')
            .then((res) => res.json())
            .then((json) => setCategories(json))
            .catch((error) => console.error('Error fetching categories:', error));
    }, []);

    const handleLogout = () => {
        setUser(null);
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container p-3">
                <Link className="navbar-brand" to="/">
                    <img src={logo} alt="logo" className="navbar-logo" />
                </Link>

                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav ms-auto">
                        <li className="nav-item dropdown">
                            <span
                                className="nav-link dropdown-toggle font-weight-bold"
                                style={{ cursor: 'pointer' }}
                            >
                                Shop
                            </span>
                            <ul className="dropdown-menu">
                                {categories.map((category) => (
                                    <li key={category}>
                                        <Link
                                            className="dropdown-item"
                                            to={`/shop/${category}`}
                                        >
                                            {category.charAt(0).toUpperCase() + category.slice(1)}
                                        </Link>
                                    </li>
                                ))}
                            </ul>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="/wishlist">
                                <i className="fas fa-heart"></i>
                                {wishlist.length > 0 && (
                                    <span className="badge bg-danger ms-1">{wishlist.length}</span>
                                )}
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="/cart">
                                <i className="fas fa-shopping-cart"></i>
                                {cart.length > 0 && (
                                    <span className="badge bg-danger ms-1">{cart.length}</span>
                                )}
                            </Link>
                        </li>
                        {user ? (
                            <li className="nav-item">
                                <span className="nav-link">Welcome, {user.username}</span>
                                <button onClick={handleLogout} className="btn btn-link nav-link">
                                    Logout
                                </button>
                            </li>
                        ) : (
                            <li className="nav-item">
                                <Link className="nav-link" to="/login">
                                    <i className="fas fa-user"></i> Login
                                </Link>
                            </li>
                        )}
                        <li className="nav-item">
                            <Link className="btn btn-outline-primary" to="/register">
                                Register
                            </Link>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    );
};

export default Header;
