// Shop.js
import React from 'react';
import { Link } from 'react-router-dom';

const Shop = () => {
    const categories = [
        { name: 'Electronics', path: '/shop/electronics' },
        { name: 'Jewelery', path: '/shop/jewelery' },
        { name: 'Men\'s Clothing', path: '/shop/men\'s-clothing' },
        { name: 'Women\'s Clothing', path: '/shop/women\'s-clothing' },
    ];

    return (
        <div className="container mt-5">
            <h2>Shop</h2>
            <div className="list-group">
                {categories.map((category, index) => (
                    <Link key={index} to={category.path} className="list-group-item list-group-item-action">
                        {category.name}
                    </Link>
                ))}
            </div>
        </div>
    );
};

export default Shop;
