// DropdownMenu.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const DropdownMenu = () => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleDropdown = () => {
        setIsOpen(prev => !prev); // Toggle the dropdown
    };

    const handleLinkClick = () => {
        setIsOpen(false); // Close dropdown when a link is clicked
    };

    return (
        <li className="nav-item dropdown">
            <button
                className="nav-link dropdown-toggle font-weight-bold"
                onClick={toggleDropdown}
                aria-expanded={isOpen}
                style={{ background: 'none', border: 'none', cursor: 'pointer' }}
            >
                Shop
            </button>
            {isOpen && (
                <ul className="dropdown-menu" style={dropdownStyle}>
                    <li>
                        <Link className="dropdown-item" to="/shop/electronics" onClick={handleLinkClick}>
                            Electronics
                        </Link>
                    </li>
                    <li>
                        <Link className="dropdown-item" to="/shop/jewelry" onClick={handleLinkClick}>
                            Jewelry
                        </Link>
                    </li>
                    <li>
                        <Link className="dropdown-item" to="/shop/men's-clothing" onClick={handleLinkClick}>
                            Men's Clothing
                        </Link>
                    </li>
                    <li>
                        <Link className="dropdown-item" to="/shop/women's-clothing" onClick={handleLinkClick}>
                            Women's Clothing
                        </Link>
                    </li>
                </ul>
            )}
        </li>
    );
};

// Inline styles for dropdown menu
const dropdownStyle = {
    position: 'absolute',
    top: '100%', // Position below the button
    left: '0',
    backgroundColor: 'white',
    border: '1px solid #ccc',
    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
    listStyle: 'none',
    padding: '0',
    margin: '0',
    zIndex: 1000, // Ensure it appears above other content
};

export default DropdownMenu;
