import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './Product.css';

const Products = ({ addToWishlist, addToCart, wishlist, cart }) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [sortedCarts, setSortedCarts] = useState([]);
    const [sortField, setSortField] = useState('total');  // Sorting field (e.g., total)
    const [sortOrder, setSortOrder] = useState('desc');   // Sorting order ('asc' or 'desc')

    // Fetch product data from API
    useEffect(() => {
        fetch('https://fakestoreapi.com/products')  // Replace with your actual API endpoint for products
            .then((response) => response.json())
            .then((data) => {
                setProducts(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching products:', error);
                setLoading(false);
            });
    }, []);

    // Fetch and sort cart data from Fakestore API
    useEffect(() => {
        fetch('https://fakestoreapi.com/carts?sort=desc')  // Fakestore carts API with sorting
            .then((res) => res.json())
            .then((json) => {
                // If sorting needs to be handled manually instead of relying on the API
                const sortedData = json.sort((a, b) => {
                    if (sortOrder === 'desc') {
                        return b[sortField] - a[sortField];  // Sort in descending order
                    } else {
                        return a[sortField] - b[sortField];  // Sort in ascending order
                    }
                });
                setSortedCarts(sortedData);
            })
            .catch((error) => {
                console.error('Error fetching cart data:', error);
            });
    }, [sortField, sortOrder]); // Rerun the effect when sortField or sortOrder changes

    // Handle sorting change
    const handleSortChange = (field) => {
        setSortField(field);
        setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');  // Toggle between asc and desc
    };

    if (loading) return <p className="container">Loading...</p>;

    return (
        <div className="container mt-4">
            <h1 className="text-center mb-5 mt-5 hover-underline">Product List</h1>

            {/* Sort Controls */}
            <div className="d-flex justify-content-center mb-4">
                <button className="btn btn-secondary me-2" onClick={() => handleSortChange('total')}>
                    Sort by Total
                </button>
                <button className="btn btn-secondary" onClick={() => handleSortChange('id')}>
                    Sort by ID
                </button>
            </div>

            {/* Display All Products */}
            <div className="row">
                {products.map((product) => (
                    <div className="col-lg-3 col-md-4 col-sm-6 mb-4" key={product.id}>
                        <div className="card h-100">
                            <img
                                src={product.image}
                                className="card-img-top"
                                alt={product.title}
                                style={{ height: '250px', objectFit: 'contain' }}
                            />
                            <div className="card-body">
                                <h5 className="card-title">{product.title}</h5>
                                <p className="card-text">${product.price}</p>
                                <Link to={`/product/${product.id}`} className="btn btn-primary mb-2 me-2">
                                    View Details
                                </Link>

                                {/* Wishlist Icon */}
                                <button
                                    className="btn btn-outline-warning me-2"
                                    onClick={() => addToWishlist(product)}
                                    disabled={wishlist.some((item) => item.id === product.id)}
                                >
                                    <i
                                        className={`fas fa-heart ${wishlist.some((item) => item.id === product.id) ? 'text-danger' : ''
                                            }`}
                                    ></i>
                                </button>

                                {/* Cart Icon */}
                                <button
                                    className="btn btn-outline-success"
                                    onClick={() => addToCart(product)}
                                    disabled={cart.some((item) => item.id === product.id)}
                                >
                                    <i
                                        className={`fas fa-shopping-cart ${cart.some((item) => item.id === product.id) ? 'text-success' : ''
                                            }`}
                                    ></i>
                                </button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Display Sorted Cart Data */}
            <h3 className="text-center mt-5">Sorted Cart Data</h3>
            <div className="row">
                {sortedCarts.map((cartItem) => (
                    <div className="col-md-4" key={cartItem.id}>
                        <div className="card">
                            <div className="card-body">
                                <h5 className="card-title">Cart ID: {cartItem.id}</h5>
                                <p className="card-text">Total: ${cartItem.total}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Products;
