// Home.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetch('https://fakestoreapi.com/products')
            .then((response) => response.json())
            .then((data) => {
                setProducts(data);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching products:', error);
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <div className="text-center mt-5">Loading products...</div>;
    }

    // Slice the products array to show only the first 4 products
    const displayedProducts = products.slice(0, 4);

    return (
        <div className="container mt-5">
            <header className="text-center mb-5">
                <h1>Welcome to ZENDO</h1>
                <p>Your one-stop shop for original clothing in Cambodia.</p>
                <p>Explore our exclusive collections from various brands!</p>
            </header>
            <div className="row mt-4">
                {displayedProducts.map((product) => (
                    <div className="col-lg-3 col-md-6 mb-4" key={product.id}>
                        <div className="card h-100">
                            <img
                                src={product.image}
                                className="card-img-top"
                                alt={product.title}
                            />
                            <div className="card-body d-flex flex-column">
                                <h5 className="card-title">{product.title}</h5>
                                <p className="card-text">${product.price}</p>
                                <div className="mt-auto">
                                    <Link to={`/shop/${product.id}`} className="btn btn-primary">
                                        Shop Now
                                    </Link>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <div className="text-center mt-5">
                <Link to="/shop" className="btn btn-outline-primary">Browse All Products</Link>
            </div>
        </div>
    );
};

export default Home;
