import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const CategoryPage = () => {
    const { category } = useParams();
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchCategoryProducts = async () => {
            try {
                const response = await fetch(`https://fakestoreapi.com/products/category/${category}`);
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchCategoryProducts();
    }, [category]);

    return (
        <div className="container my-4">
            <h2 className="text-center mb-4">{category.charAt(0).toUpperCase() + category.slice(1)}</h2>
            <div className="row">
                {products.map((product) => (
                    <div key={product.id} className="col-lg-3 col-md-4 col-sm-6 mb-4">
                        <div className="card h-100">
                            <img src={product.image} className="card-img-top" alt={product.title} style={{ height: '250px', objectFit: 'contain' }} />
                            <div className="card-body d-flex flex-column">
                                <h5 className="card-title">{product.title}</h5>
                                <p className="card-text">{product.description.slice(0, 100)}...</p>
                                <p className="text-muted">${product.price}</p>
                                <Link to={`/product/${product.id}`} className="btn btn-primary mt-auto">View Details</Link>
                            </div>
                            {/* Wishlist Icon */}
                            <button
                                className="btn btn-outline-warning me-2"
                                onClick={() => addToWishlist(product)}
                                disabled={wishlist.some((item) => item.id === product.id)}
                            >
                                <i className={`fas fa-heart ${wishlist.some((item) => item.id === product.id) ? 'text-danger' : ''}`}></i>
                            </button>

                            {/* Cart Icon */}
                            <button
                                className="btn btn-outline-success"
                                onClick={() => addToCart(product)}
                                disabled={cart.some((item) => item.id === product.id)}
                            >
                                <i className={`fas fa-shopping-cart ${cart.some((item) => item.id === product.id) ? 'text-success' : ''}`}></i>
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default CategoryPage;
